import { useEffect, useState } from 'react';
import h from '../Header/Header.module.scss'
import del from '../Header/img/del.svg'
import axios from 'axios';
import { useFavoritesContext } from '../../FavoritesContext';

export default function Tovar ({onRemoveFromFavorites ,...info}) {


    const [imageUrls, setImageUrls] = useState([]);

    const { favorites, setFavorites } = useFavoritesContext();

    async function favoritesDelete(id) {
        // ... (Ваш текущий код)
        onRemoveFromFavorites(info.id); // Вызываем функцию из Card.jsx, чтобы обновить состояние сердечка
      }


    useEffect(() => {

      axios.get('http://127.0.0.1:8000/api/goods/?is_favorited=true', {

      headers: {
          'Content-Type': 'application/json , multipart/form-data',
          'authorization': `Token ${tokenTwo}`
      }

      })

      .then((res) => {

        setImageUrls(res.data.results)

       })

      .catch((err) => console.error(err))

  }, [])


  const [heart, setHeart] = useState(info.is_favorited)


  console.log(heart);

  async function favoritesDelete(id) { 

    setHeart(false);

    await axios
      .delete(`http://127.0.0.1:8000/api/goods/${info.id}/favorite/`, {
        headers: {
          'content-type': 'application/json',
          authorization: `Token ${tokenTwo}`,
        },
      })

      .catch((err) => console.error(err));

    await axios
      .get('http://127.0.0.1:8000/api/goods/?is_favorited=true', {
        headers: {
          'content-type': 'application/json',
          authorization: `Token ${tokenTwo}`,
        },
      })
      .then((res) => {
        setFavorites(res.data.results); // Обновление списка избранных товаров в контексте
      })
      .catch((err) => console.error(err));

  }



  const tokenTwo = localStorage.getItem('token')

    return (

    <div className={h.nav__kar__item}>

        <div className={h.nav__kar__item_info}>

        {imageUrls.map((item, index) => (

          item.images.map((image, idx) => (

            <img key={index + '-' + idx} src={image.images} alt={`Image ${index}-${idx}`} className={h.nav__kar__item_info_img} />

          ))

        ))}

            <div className={h.nav__kar__item_info_titles}>
                <p>{info.price} руб.</p>
                <p>{info.title}</p>
            </div>

            <img src={del} id={info.id}
             onClick={ (event) => favoritesDelete(event.currentTarget.id) }
             className={h.nav__kar__item_info__del} alt="svg"
             />


        </div>

    </div>

    )
}
