import { BrowserRouter, Route, Routes } from 'react-router-dom'

import './index.scss';
import Header from './components/Header/Header';
import Home from './pages/Home';
import About from './pages/about/About';
import KidsMenu from './pages/KidsMenu/KidsMenu';
import InterCard from './pages/InterCard/InterCard';
import ScrollToTop from './ScrollTop';
import Combo from './pages/combo/Combo';
import Reg from './pages/Auth/Reg';
import Login from './pages/Auth/Login';
import { useEffect, useState } from 'react';
import Footer from './components/Footer/Footer';
import Basket from './pages/Basket/Basket';

function App() {


  const [isActive , setIsActive] = useState(false)

  useEffect(() => {

    // При монтировании компонента проверяем, есть ли данные об авторизации в localStorage
    const isUserAuthorized = localStorage.getItem('isAuthorized') === 'true';
    setIsActive(isUserAuthorized);

  }, []);

  const signOut = () => {
    const isUserAuthorizedOut = localStorage.setItem('isAuthorized') === 'false';
  }

  const handleLogout = () => {
    setIsAuthorized(false);
    localStorage.setItem('isAuthorized', 'false'); // Сохраняем состояние в localStorage
  };

  const [token, setToken] = useState('')

  return (



    <BrowserRouter>

      <div className="App">

        <ScrollToTop />

         <Header isActive={isActive} setIsActive={setIsActive} signOut={signOut} />

        <Routes>

        <Route path='/'  element={<Home />} />

        <Route path='/register'  element={<Reg />} />

        <Route path='/login'  element={<Login setToken={setToken} setIsActive={setIsActive} />} />

        <Route path='/combo'  element={<Combo />} />

        <Route path='/about'  element={<About />} />

        <Route path='/kidsmenu'  element={<KidsMenu />} />

        <Route path='/intercard'  element={<InterCard />} />

        <Route path='/basket'  element={<Basket />} />

        </Routes>

        <Footer />

      </div>

    </BrowserRouter>


  );

}

export default App;
