import b from './Basket.module.scss';

import h from '../../components/Header/Header.module.scss';
import X from '../../components/Header/img/del.svg';
import axios from 'axios';
import { useState } from 'react';

export default function BasketTovar({ removeBasket, ...info }) {

  const [count, setCount] = useState(1)
  const [countInfo, setCountInfo] = useState([]) 

  async function countPlus(id) {

     try {

         await axios.patch(`http://127.0.0.1:8000/api/goods/${id}/shopping_cart/`, 

         {

             count: count + 1,

         },

         {

             headers: {
                 'content-type': 'application/json',
                 authorization: `Token ${tokenTwo}`,
             },

         });

         const updatedCountInfo = countInfo.map((item) =>
         item.goods.id === id ? { ...item, count: item.count + 1 } : item
         );

         setCountInfo(updatedCountInfo);

         const updatedPrice = info.price + info.price / info.count;
         setCountInfo((prevInfo) => ({ ...prevInfo, count: prevInfo.count + 1, price: updatedPrice }));

     } catch (err) {
         console.error(err);
     }
  }

  const tokenTwo = localStorage.getItem('token')


  return (
    <div className={b.map__item}>
      <div className={b.map__item__flex}>

        {info && info.images && info.images[0] && info.images[0].images ? (

        <img src={info.images[0].images} alt="img" className={h.nav__kar__item_info_img} />

        ) : (

        <p>No image available</p>

        )}

        <div className={b.map__item__info}>
          <p>{info.title}</p>
          <p>{info.price} руб.</p>
        </div>
      </div>


      <div className={h.nav__kar__item__fun}>

        <div className={h.nav__kar__item__fun__add}>-</div>

        <h3>{count}</h3>

        <div className={h.nav__kar__item__fun__add}

        id={info.id}
        onClick={() => countPlus(info.id)}

        >+</div>

        <img
          src={X}
          className={h.nav__danniy__header__exit}
          alt="exit"
          id={info.id}
          onClick={() => removeBasket(info.id)}
        />
      </div>
    </div>
  );
}
