import { createContext, useState, useContext, useEffect } from 'react';

const HeartContext = createContext();

export function useHeartContext() {
  return useContext(HeartContext);
}

export function HeartProvider({ children }) {
  const [favoriteIds, setFavoriteIds] = useState([]); // Список идентификаторов избранных товаров

  // Функция для добавления/удаления товара из избранного
  function toggleFavorite(id) {
    if (favoriteIds.includes(id)) {
      setFavoriteIds(favoriteIds.filter((favoriteId) => favoriteId !== id));
    } else {
      setFavoriteIds([...favoriteIds, id]);
    }
  }

  // Загрузка данных из localStorage при монтировании
  useEffect(() => {
    const favoriteIdsFromStorage = localStorage.getItem('favoriteIds');
    if (favoriteIdsFromStorage) {
      setFavoriteIds(JSON.parse(favoriteIdsFromStorage));
    }
  }, []);

  // Сохранение данных в localStorage при обновлении favoriteIds
  useEffect(() => {
    localStorage.setItem('favoriteIds', JSON.stringify(favoriteIds));
  }, [favoriteIds]);

  // Возвращаем setHeart вместе с favoriteIds
  return (
    <HeartContext.Provider value={{ favoriteIds, toggleFavorite, setHeart }}>
      {children}
    </HeartContext.Provider>
  );
}
